mapper.select:
		SELECT a.cityCode  , a.gunCode , a.dongCode, a.cityN , a.gunN , a.dongN , IFNULL(tem.cityCount,0) as cityCount
        FROM address_code AS a 
        LEFT JOIN  
        (
        		select tem.* from (
				select count(a.cityCode) as cityCount ,a.cityCode 
				from web_member AS a 
				group by a.cityCode
				) as tem
        ) as tem

		ON a.cityCode = tem.cityCode

        where 1=1 
		and tem.cityCode = #{cityCode}
		and tem.gunCode = #{gunCode}
		and tem.dongCode = #{dongCode}

		GROUP BY a.cityCode

SELECT a.cityCode , a.gunCode , a.dongCode, a.cityN , a.gunN , a.dongN , IFNULL(tem.gunCount,0) as gunCount
FROM address_code AS a LEFT JOIN
( select tem.* from ( select a.cityCode , a.gunCode ,a.gunN , count(a.gunCode) as gunCount from web_member AS a group by a.cityCode ,a.gunCode having 1=1
and a.cityCode = #{cityCode}
) as tem ) as tem
ON a.cityCode = tem.cityCode and a.gunCode = tem.gunCode
where 1=1 and a.cityCode = #{cityCode}
GROUP BY a.cityCode ,a.gunCode order by gunN asc

		SELECT a.cityCode  , a.gunCode , a.dongCode, a.cityN , a.gunN , a.dongN , IFNULL(tem.gunCount,0) as gunCount

        FROM address_code AS a 
        LEFT JOIN  

        (
			select tem.* from (
				select a.cityCode , a.gunCode ,a.gunN , count(a.gunCode) as gunCount
				from web_member AS a 
				group by a.cityCode ,a.gunCode
				having 1=1

				 and a.cityCode = #{cityCode}

			) as tem
        ) as tem


		ON a.cityCode = tem.cityCode
        and  a.gunCode = tem.gunCode

        where 1=1 

        and a.cityCode = #{cityCode}
		and a.gunCode = #{gunCode}
		and a.dongCode = #{dongCode}
		GROUP BY a.cityCode ,a.gunCode 
        order by gunN asc

SELECT a.cityCode , a.gunCode , a.dongCode, a.cityN , a.gunN , a.dongN , IFNULL(tem.dongCount,0) as dongCount FROM address_code AS a LEFT JOIN
( select tem.* from ( select a.cityCode , a.gunCode ,a.gunN , a.dongCode , count(a.dongCode) as dongCount from web_member AS a group by a.cityCode ,a.gunCode ,a.dongCode having 1=1
and a.cityCode = #{cityCode} and a.gunCode = #{gunCode}
) as tem ) as tem
ON a.cityCode = tem.cityCode and a.gunCode = tem.gunCode and a.dongCode = tem.dongCode
where 1=1 and a.cityCode = #{cityCode} and a.gunCode = #{gunCode}
GROUP BY a.cityCode , a.gunCode ,a.dongCode order by dongN asc

		SELECT a.cityCode  , a.gunCode , a.dongCode, a.cityN , a.gunN , a.dongN , IFNULL(tem.dongCount,0) as dongCount
        FROM address_code AS a 
        LEFT JOIN  

        (
			select tem.* from (
				select a.cityCode , a.gunCode ,a.gunN , a.dongCode , count(a.dongCode) as dongCount
				from web_member AS a 
				group by a.cityCode ,a.gunCode ,a.dongCode
				having 1=1

				 and a.cityCode = #{cityCode}
                 and a.gunCode = #{gunCode}

			) as tem
        ) as tem


		ON a.cityCode = tem.cityCode
        and  a.gunCode = tem.gunCode
        and  a.dongCode = tem.dongCode

        where 1=1 
		and a.cityCode = #{cityCode}
		and a.gunCode = #{gunCode}
		and a.dongCode = #{dongCode}

		GROUP BY a.cityCode , a.gunCode ,a.dongCode 
        order by dongN asc

SELECT ifnull(count(seq),0) FROM web_member
where 1=1
and regDt is not null

and regDt >= #{startDate}
and regDt < #{endDate}

SELECT ifnull(count(seq),0) FROM web_member
where 1=1
and regDt is not null
and dangwon = '일반'

and regDt >= #{startDate}
and regDt < #{endDate}

SELECT  ifnull(count(DATE_FORMAT(regDt, '%Y-%m-%d')),0)    FROM web_member
where 1=1
and regDt is not null
and dangwon = '책임'

and regDt >= #{startDate}
and regDt <= #{endDate}

		select A.selectedDate, IFNULL(D.targetCount,0) selectedDateCount , IFNULL(F.targetCount,0) selectedDateCountC , IFNULL(E.targetCount,0) selectedDateCountN from (

			select selectedDate  from 
			(select adddate('1970-01-01',t4.i*10000 + t3.i*1000 + t2.i*100 + t1.i*10 + t0.i) selectedDate from
			 (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t0,
			 (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t1,
			 (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t2,
			 (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t3,
			 (select 0 i union select 1 union select 2 union select 3 union select 4 union select 5 union select 6 union select 7 union select 8 union select 9) t4) v
				where 1=1

				and v.selectedDate >= #{startDate}
				and v.selectedDate <= #{endDate}


			) A left join 
			(
				SELECT DATE_FORMAT(regDt, '%Y-%m-%d') as targetDate , count(DATE_FORMAT(regDt, '%Y-%m-%d') )  as targetCount  FROM web_member
				where 1=1


				and cityCode = #{cityCode}

				and regDt is not null

				and regDt >= #{startDate}
				and regDt <= #{endDate}


				group by DATE_FORMAT(regDt, '%Y-%m-%d') asc
			)	D
			ON A.selectedDate = D.targetDate

            left join 
			(
				SELECT DATE_FORMAT(regDt, '%Y-%m-%d') as targetDate , count(DATE_FORMAT(regDt, '%Y-%m-%d') )  as targetCount  FROM web_member
				where 1=1

				and cityCode = #{cityCode}

				and dangwon = '일반'
				and regDt is not null

				and regDt >= #{startDate}
				and regDt <= #{endDate}


				group by DATE_FORMAT(regDt, '%Y-%m-%d') asc
			)	F
			ON A.selectedDate = F.targetDate

                        left join 
			(
				SELECT DATE_FORMAT(regDt, '%Y-%m-%d') as targetDate , count(DATE_FORMAT(regDt, '%Y-%m-%d') )  as targetCount  FROM web_member
				where 1=1

				and cityCode = #{cityCode}

				and dangwon = '책임'
				and regDt is not null

					and regDt >= #{startDate}
					and regDt <= #{endDate}


				group by DATE_FORMAT(regDt, '%Y-%m-%d') asc
			)	E
			ON A.selectedDate = E.targetDate

			order by A.selectedDate asc

SELECT districtName, districtCode, sum(districtCount) districtCount,cityCode FROM ( SELECT z1.*, z2.district_name districtName ,z2.code districtCode, ( select count(seq) from web_member m1 where 1=1 and m1.cityCode = #{cityCode} and m1.gunCode = z1.gunCode and m1.dongCode = z1.dongCode
) districtCount
FROM (SELECT cityCode, cityN, gunCode, gunN, dongCode, dongN, district_code FROM koreacrpgp.address_code WHERE 1 = 1 AND cityCode = #{cityCode} GROUP BY cityCode , cityN , gunCode , gunN , dongCode, dongN, district_code) z1 LEFT JOIN district_code z2 ON z1.district_code = z2.code
) final group by districtName, districtCode

SELECT z1.*, z2.district_name districtName ,z2.code districtCode, ( select count(seq) from web_member m1 where 1=1 and m1.cityCode = #{cityCode} and m1.gunCode = z1.gunCode and m1.dongCode = z1.dongCode
) dongCount
FROM (SELECT cityCode, cityN, gunCode, gunN, dongCode, dongN, district_code FROM koreacrpgp.address_code WHERE 1 = 1 AND cityCode = #{cityCode} and district_code = #{districtCode} GROUP BY cityCode , cityN , gunCode , gunN , dongCode, dongN, district_code) z1 LEFT JOIN district_code z2 ON z1.district_code = z2.code


SELECT count(seq) FROM koreacrpgp.web_member
where 1=1
and cityCode = #{cityCode}


	and districtCode =  #{districtCode}






	and dongCode = #{dongCode}


